import React from 'react';
import './App.css';
import axios from 'axios';
import FlatList from 'flatlist-react';
import * as ReactBootStrap from 'react-bootstrap';
// import ReactSearchBox from 'react-search-box'
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
      searchTerm: '',
      players : []
    };
  }

  componentDidMount() {
    axios.get(`http://localhost:10010/films?page=1&limit=10`,{headers : {
    "Access-Control-Allow-Origin": "*",
    "Access-Control-Allow-Methods": "GET,PUT,POST,DELETE,PATCH,OPTIONS"
    }
  })
      .then(res => {
        // const players = res.data;
        console.log(res);
        this.setState({ players:res.data.data });
      }).catch (error => {
        console.log('.....error........',error);
      })
  }
  editSearchTerm = (e) => {
    this.setState({searchTerm : e.target.value})
  }
  renderPlayer = (player,index) => {

    return(
      <tr key = {index}>
        <td>{player.track}</td>
        <td>{player.album}</td>
        <td>{player.bytes}</td>
        <td>{player.composer}</td>
        <td>{player.media_type}</td>
        <td>{player.milliseconds}</td>
        <td>{player.unit_price}</td>
      </tr>
    )
  }
  render() {
    const filteredData = ()=> {
      axios.get(`http://localhost:10010/films?page=1&limit=10&filter=track&search=break`,{headers : {
      "Access-Control-Allow-Origin": "*",
      "Access-Control-Allow-Methods": "GET,PUT,POST,DELETE,PATCH,OPTIONS"
      }
    })
        .then(res => {
          // const players = res.data;
          console.log(res);
          this.setState({ players:res.data.data });
        }).catch (error => {
          console.log('.....error........',error);
        })

    } 
    return (
      <div className="App">
      <input type = 'text' value = {this.state.searchTerm} onChange = {this.editSearchTerm} placeholder = 'Search' />
      <button onClick = {filteredData}>Search</button>
      <ReactBootStrap.Table striped bordered hover variant="dark">
  <thead>
    <tr>
      <th>Track</th>
      <th>Album</th>
      <th>Bytes</th>
      <th>Composer</th>
      <th>Media Type</th>
      <th>Milli Seconds</th>
      <th>Unit Price</th>
    </tr>
  </thead>
  <tbody>
  <FlatList
          list={this.state.players}
          renderItem={this.renderPlayer}
          renderWhenEmpty={() => <div>List is empty!</div>}
          sortBy={["track", {key: "track", ascending: true}]}
          // groupBy={person => person.info.age > 18 ? 'Over 18' : 'Under 18'}
        />
    {/* {this.state.players.map(this.renderPlayer)} */}
  </tbody>
</ReactBootStrap.Table>
    </div>
    );
  }
}
